generated.css:
body {
  font-family: "DM Sans", sans-serif;
}

.wrapper {
  background: hsl(27deg, 66%, 92%);
  height: 100%;
  width: 100%;
}
.wrapper main {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  height: 98%;
}
.wrapper footer {
  height: 2%;
}

body {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  width: 100vw;
  height: 100vh;
}

.card {
  background: hsl(33deg, 100%, 98%);
  border-radius: 20px;
  width: 40%;
  padding: 3%;
}
.card h2 {
  margin-top: 0;
  color: hsl(25deg, 47%, 15%);
}
.card .bottom_bar {
  display: flex;
  justify-content: space-between;
  padding-top: 4%;
  border-top: 2px solid hsl(27deg, 66%, 92%);
}
.card .bottom_bar .total, .card .bottom_bar .last {
  flex-direction: column;
}
.card .bottom_bar .total p, .card .bottom_bar .last p {
  color: hsl(28deg, 10%, 53%);
  margin: 0;
}
.card .bottom_bar .total .dark, .card .bottom_bar .last .dark {
  margin: 0;
  color: hsl(25deg, 47%, 15%);
  font-weight: 700;
  font-size: 18px;
}
.card .bottom_bar .total .dark:last-of-type, .card .bottom_bar .last .dark:last-of-type {
  font-size: 36px;
}
.card .bottom_bar .total {
  display: flex;
  justify-content: start;
  align-items: flex-start;
}
.card .bottom_bar .total p {
  margin-bottom: 5%;
}
.card .bottom_bar .last {
  display: flex;
  justify-content: end;
  align-items: flex-end;
}
.card .bottom_bar .last .dark {
  margin-bottom: 5%;
}

.top_bar {
  display: flex;
  justify-content: center;
  align-items: start;
  flex-direction: column;
  background-color: hsl(10deg, 79%, 65%);
  border-radius: 20px;
  width: 40%;
  position: relative;
  padding: 3%;
  margin-bottom: 2%;
}
.top_bar p {
  color: hsl(33deg, 100%, 98%);
  margin: 1%;
}
.top_bar p:last-of-type {
  font-size: 22px;
  font-weight: 700;
}
.top_bar img {
  position: absolute;
  right: 5%;
}

.bars {
  display: flex;
  justify-content: space-evenly;
  align-items: end;
  margin-bottom: 6%;
  height: 300px;
  display: flex;
  flex-direction: row;
  position: relative;
}
.bars .bar {
  border-radius: 10px;
  width: 10%;
  background-color: hsl(10deg, 79%, 65%);
  position: relative;
  transition: background ease 0.3s;
}
.bars .bar .amount {
  display: none;
  background: hsl(25deg, 47%, 15%);
  border-radius: 10px;
  color: hsl(27deg, 66%, 92%);
  position: absolute;
  padding: 7%;
  top: -55px;
  left: 50%;
  transform: translate(-50%, 0);
  white-space: nowrap;
  transition: all ease 0.3s;
}
.bars .bar:hover > .amount {
  display: block;
}
.bars .bar:hover {
  cursor: pointer;
  background-color: hsla(10deg, 79%, 65%, 0.5);
}
.bars .bar:hover > .amount {
  display: block;
}
.bars .week {
  position: absolute;
  width: 100%;
  bottom: -14%;
  display: flex;
  flex-direction: row;
  justify-content: space-evenly;
}
.bars .week p {
  color: hsl(28deg, 10%, 53%);
  width: 10%;
  text-align: center;
}

@media screen and (max-width: 425px) {
  .top_bar {
    width: 80%;
    margin-bottom: 5%;
    padding: 5%;
  }
  .card {
    width: 80%;
    padding: 5%;
  }
  .bars {
    margin-bottom: 15%;
  }
  .bars .bar {
    border-radius: 5px;
  }
  .bars .bar .amount {
    padding: 15%;
    z-index: 2;
  }
}
@media screen and (max-width: 768px) {
  .top_bar {
    width: 80%;
  }
  .card {
    width: 80%;
  }
  .bars {
    margin-bottom: 10%;
  }
}
